const BrazilMap: React.FC<BrazilMapProps> = ({ data = {}, selectedState, onSelectState, heatmapMode = false }) => {
    const getFillColor = (uf: string) => {
        if (selectedState === uf) {
            return '#2563eb'; // blue-600
        }

        if (heatmapMode) {
            const count = data[uf] || 0;
            const max = Object.values(data).length > 0 ? Math.max(...Object.values(data)) : 1;
            const ratio = count / max;
            
            if (count === 0) return '#e5e7eb'; // Gray 200 for empty
            if (ratio < 0.2) return '#dbeafe'; 
            if (ratio < 0.4) return '#bfdbfe';
            if (ratio < 0.6) return '#93c5fd';
            if (ratio < 0.8) return '#60a5fa';
            return '#3b82f6';
        }

        return '#e5e7eb'; // default gray
    };

    return (
        <div className="w-full h-full flex items-center justify-center p-2">
            <svg
                viewBox="0 0 612 650"
                className="w-full h-full max-h-full drop-shadow-lg"
                xmlns="http://www.w3.org/2000/svg"
                style={{ filter: 'drop-shadow(0px 4px 6px rgba(0, 0, 0, 0.1))' }}
            >
                <g id="map" className="transition-transform duration-500 ease-out">
                    {STATES_DATA.map((state) => (
                        <path
                            key={state.id}
                            id={state.id}
                            name={state.name}
                            d={state.d}
                            fill={getFillColor(state.id)}
                            stroke="white"
                            strokeWidth={selectedState === state.id ? 2 : 0.8}
                            onClick={() => onSelectState && onSelectState(state.id)}
                            className="cursor-pointer transition-all duration-300 hover:brightness-90 hover:scale-[1.01] origin-center focus:outline-none"
                            style={{ 
                                outline: 'none',
                                vectorEffect: 'non-scaling-stroke'
                            }}
                        >
                            <title>{state.name}: {data[state.id] || 0} representantes</title>
                        </path>
                    ))}
                </g>
            </svg>
        </div>
    );
};

export default BrazilMap;
